import {
  Checkbox,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
  Typography,
} from "@mui/material";
import { Fragment } from "react";

const textColor = "#504746";
const textColorLight = "#FFFFFF";
const backgroundColor = "#196F6E";
const backgroundColorLight = "#56BAA2";
const actionColor = "#5CA0C9";

const TransactionsSelector = ({
  transactions,
  isTransactionToBeExported,
  handleChangeDebitor,
  selectTransaction,
  DEBITORS_ACCOUNT,
  selectAllTransactions,
  isAllTransactionsSelected,
  isGrouped,
  transactionTotals,
  isTransactionInvalid,
  debitor,
  getTransactionsToExportTotal,
}) => {
  const totalTransactionsToExport = getTransactionsToExportTotal();
  return (
    <TableContainer sx={{ maxHeight: "calc(100vh - 250px)" }}>
      <Table stickyHeader>
        <TableHead
          sx={{
            position: "sticky",
            top: 0,
            boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.25)",
            zIndex: 10000,
          }}
        >
          <TableRow>
            <TableCell
              align="center"
              component="th"
              scope="row"
              style={{ background: backgroundColor }}
            >
              <Checkbox
                style={{ color: actionColor }}
                checked={isAllTransactionsSelected()}
                onClick={(e) => selectAllTransactions(e)}
              />
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{
                fontWeight: "bold",
                color: textColorLight,
                background: backgroundColor,
              }}
            >
              DOCUMENT
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{
                fontWeight: "bold",
                color: textColorLight,
                background: backgroundColor,
              }}
            >
              FOURNISSEUR
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{
                fontWeight: "bold",
                color: textColorLight,
                background: backgroundColor,
              }}
            >
              IBAN
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{
                fontWeight: "bold",
                color: textColorLight,
                background: backgroundColor,
              }}
            >
              DATE EXECUTION
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{
                fontWeight: "bold",
                color: textColorLight,
                background: backgroundColor,
              }}
            >
              BIC
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{
                fontWeight: "bold",
                color: textColorLight,
                background: backgroundColor,
              }}
            >
              MONTANT
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {Object.entries(
            transactions.reduce(
              (
                acc,
                {
                  Res_Id,
                  nomCrediteur,
                  ibanCrediteur,
                  bicCrediteur,
                  ttc,
                  debitor_id,
                  date_execution,
                }
              ) => {
                if (!acc[nomCrediteur]) {
                  acc[nomCrediteur] = [];
                }
                acc[nomCrediteur].push({
                  Res_Id,
                  nomCrediteur,
                  ibanCrediteur,
                  bicCrediteur,
                  ttc,
                  debitor_id,
                  date_execution,
                });
                return acc;
              },
              {}
            )
          ).map(([nomCrediteur, transactions, ttc, Res_Id]) => {
            return (
              <Fragment key={nomCrediteur}>
                <TableRow key={nomCrediteur}>
                  <TableCell colSpan={7} align="left" sx={{ margin: "34px 0" }}>
                    <Typography
                      component="span"
                      sx={{
                        display: "inline-block",
                        background: backgroundColor,
                        padding: "10px",
                      }}
                    >
                      <strong style={{ color: textColorLight }}>
                        Pour le fournisseur :{" "}
                      </strong>
                      {nomCrediteur}
                    </Typography>
                    <FormControl
                      sx={{ margin: "34px 0" }}
                      fullWidth
                      required={totalTransactionsToExport[nomCrediteur] > 0}
                      disabled={
                        totalTransactionsToExport[nomCrediteur] === undefined
                      }
                    >
                      <InputLabel style={{ color: "#00517b" }}>
                        Debiteur
                      </InputLabel>
                      <Select
                        value={
                          debitor.find((d) => d.nomCrediteur === nomCrediteur)
                            ?.debitor_id || ""
                        }
                        onChange={handleChangeDebitor(
                          transactions,
                          nomCrediteur
                        )}
                        label="Debiteur"
                        variant="outlined"
                      >
                        {DEBITORS_ACCOUNT.map(
                          ({ id, nomDebiteur, IBAN, BIC, format }) => (
                            <MenuItem key={id} value={id}>
                              {`${nomDebiteur} - ${IBAN} - ${BIC} - ${format}`}
                            </MenuItem>
                          )
                        )}
                      </Select>
                    </FormControl>
                  </TableCell>
                </TableRow>
                {transactions.map(
                  ({
                    Res_Id,
                    nomCrediteur,
                    ibanCrediteur,
                    bicCrediteur,
                    ttc,
                    date_execution,
                  }) => (
                    <Tooltip
                      key={Res_Id}
                      arrow
                      title={
                        isTransactionInvalid(nomCrediteur, ttc)
                          ? `Cette transaction à un montant négatif (${Number(
                              ttc
                            ).toFixed(
                              2
                            )}) et ne peut pas être inclue dans le fichier d'export`
                          : `Total transactions : ${
                              isGrouped
                                ? Number(
                                    transactionTotals[nomCrediteur]
                                  ).toFixed(2)
                                : Number(ttc).toFixed(2)
                            } pour ${nomCrediteur}`
                      }
                    >
                      <TableRow
                        key={Res_Id}
                        style={
                          isTransactionInvalid(nomCrediteur, ttc)
                            ? { background: "rgba(241, 102, 83, 0.2)" }
                            : { background: "rgba(27, 94, 32, 0.2)" }
                        }
                      >
                        <TableCell component="th" scope="row" align="center">
                          <Checkbox
                            checked={
                              isTransactionToBeExported(Res_Id) &&
                              isTransactionInvalid(nomCrediteur, ttc) === false
                            }
                            disabled={isTransactionInvalid(nomCrediteur, ttc)}
                            onClick={selectTransaction(Res_Id)}
                          />
                        </TableCell>
                        <TableCell component="th" scope="row" align="center">
                          {Res_Id}
                        </TableCell>
                        <TableCell component="th" scope="row" align="center">
                          {nomCrediteur}
                        </TableCell>
                        <TableCell component="th" scope="row" align="center">
                          {ibanCrediteur}
                        </TableCell>
                        <TableCell component="th" scope="row" align="center">
                          {date_execution}
                        </TableCell>
                        <TableCell component="th" scope="row" align="center">
                          {bicCrediteur}
                        </TableCell>
                        <TableCell component="th" scope="row" align="center">
                          {ttc.toFixed(2)}
                        </TableCell>
                      </TableRow>
                    </Tooltip>
                  )
                )}
                {totalTransactionsToExport[nomCrediteur] > 0 && (
                  <TableRow>
                    <TableCell colSpan={7} align="right">
                      <Typography sx={{ fontWeight: "bold", color: "#00517b" }}>
                        TOTAL {nomCrediteur} :{" "}
                        {totalTransactionsToExport[nomCrediteur].toFixed(2)}
                      </Typography>
                    </TableCell>
                  </TableRow>
                )}
              </Fragment>
            );
          })}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TransactionsSelector;
