import {
  Checkbox,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
  Typography,
} from "@mui/material";
import { DEVISE } from "../../_config";

const TransactionsSelector = ({
  transactions,
  isTransactionToBeExported,
  handleChangeDebitor,
  selectTransaction,
  DEBITORS_ACCOUNT,
  selectAllTransactions,
  isAllTransactionsSelected,
  isGrouped,
  transactionTotals,
  isTransactionInvalid,
  debitor,
  getTransactionsToExportTotal,
}) => {
  const totalTransactionsToExport = getTransactionsToExportTotal();
  return (
    <TableContainer>
      <Table>
        <TableHead
          sx={{ position: "sticky", top: 0, zIndex: 1, background: "#56baa2" }}
        >
          <TableRow style={{ background: "#56baa2" }}>
            <TableCell align="left" component="th" scope="row">
              <Checkbox
                color="primary"
                checked={isAllTransactionsSelected()}
                onClick={(e) => selectAllTransactions(e)}
              />
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold", color: "#006cb5" }}
            >
              Res Id
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold", color: "#006cb5" }}
            >
              Fournisseur
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold", color: "#006cb5" }}
            >
              IBAN
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold", color: "#006cb5" }}
            >
              DATE EXECUTION
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold", color: "#006cb5" }}
            >
              BIC
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold", color: "#006cb5" }}
            >
              Montant
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {Object.entries(
            transactions.reduce(
              (
                acc,
                {
                  Res_Id,
                  nomCrediteur,
                  ibanCrediteur,
                  bicCrediteur,
                  ttc,
                  debitor_id,
                  date_execution,
                }
              ) => {
                if (!acc[nomCrediteur]) {
                  acc[nomCrediteur] = [];
                }
                acc[nomCrediteur].push({
                  Res_Id,
                  nomCrediteur,
                  ibanCrediteur,
                  bicCrediteur,
                  ttc,
                  debitor_id,
                  date_execution,
                });
                return acc;
              },
              {}
            )
          ).map(([nomCrediteur, transactions, ttc, Res_Id]) => {
            return (
              <>
                <TableRow key={nomCrediteur}>
                  <TableCell colSpan={7} align="left" sx={{ margin: "34px 0" }}>
                    <strong style={{ color: "#00517b" }}>
                      Pour le fournisseur :{" "}
                    </strong>
                    {nomCrediteur}
                    <FormControl
                      sx={{ margin: "34px 0" }}
                      fullWidth
                      required={totalTransactionsToExport[nomCrediteur] > 0}
                      disabled={
                        totalTransactionsToExport[nomCrediteur] === undefined
                      }
                    >
                      <InputLabel style={{ color: "#00517b" }}>
                        Debiteur
                      </InputLabel>
                      <Select
                        value={
                          debitor.find((d) => d.nomCrediteur === nomCrediteur)
                            ?.debitor_id || ""
                        }
                        onChange={handleChangeDebitor(
                          transactions,
                          nomCrediteur
                        )}
                        label="Debiteur"
                        variant="outlined"
                      >
                        {DEBITORS_ACCOUNT.map(
                          ({ id, nomDebiteur, IBAN, BIC, format }) => (
                            <MenuItem key={id} value={id}>
                              {`${nomDebiteur} - ${IBAN} - ${BIC} - ${format}`}
                            </MenuItem>
                          )
                        )}
                      </Select>
                    </FormControl>
                  </TableCell>
                </TableRow>
                {transactions.map(
                  ({
                    Res_Id,
                    nomCrediteur,
                    ibanCrediteur,
                    bicCrediteur,
                    ttc,
                    debitor_id,
                    date_execution,
                  }) => (
                    <Tooltip
                      key={Res_Id}
                      arrow
                      title={
                        isTransactionInvalid(nomCrediteur, ttc)
                          ? `Cette transaction à un montant négatif (${Number(
                              ttc
                            ).toFixed(
                              2
                            )} ${DEVISE}) et ne peut pas être inclue dans le fichier d'export`
                          : `Total transactions : ${
                              isGrouped
                                ? Number(
                                    transactionTotals[nomCrediteur]
                                  ).toFixed(2)
                                : Number(ttc).toFixed(2)
                            } ${DEVISE} pour ${nomCrediteur}`
                      }
                    >
                      <TableRow
                        key={Res_Id}
                        style={
                          isTransactionInvalid(nomCrediteur, ttc)
                            ? { background: "rgba(241, 102, 83, 0.2)" }
                            : { background: "rgba(27, 94, 32, 0.2)" }
                        }
                      >
                        <TableCell component="th" scope="row">
                          <Checkbox
                            checked={
                              isTransactionToBeExported(Res_Id) &&
                              isTransactionInvalid(nomCrediteur, ttc) === false
                            }
                            disabled={isTransactionInvalid(nomCrediteur, ttc)}
                            onClick={selectTransaction(Res_Id)}
                          />
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {Res_Id}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {nomCrediteur}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {ibanCrediteur}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {date_execution}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {bicCrediteur}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {ttc.toFixed(2)}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {/* <FormControl
                        fullWidth
                        required={isTransactionToBeExported(Res_Id)}
                        error={
                          debitor_id.length === 0 &&
                          isTransactionToBeExported(Res_Id)
                        }
                      ></FormControl> */}
                        </TableCell>
                      </TableRow>
                    </Tooltip>
                  )
                )}
                {totalTransactionsToExport[nomCrediteur] > 0 && (
                  <TableRow>
                    <TableCell colSpan={7} align="right">
                      <Typography sx={{ fontWeight: "bold", color: "#00517b" }}>
                        TOTAL {nomCrediteur} :{" "}
                        {totalTransactionsToExport[nomCrediteur].toFixed(2)}
                      </Typography>
                    </TableCell>
                  </TableRow>
                )}
              </>
            );
          })}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TransactionsSelector;
