import {
  Checkbox,
  Container,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
} from "@mui/material";
import { DEVISE } from "../../_config";

const TransactionsSelector = ({
  transactions,
  isTransactionToBeExported,
  handleChangeDebitor,
  selectTransaction,
  DEBITORS_ACCOUNT,
  selectAllTransactions,
  isAllTransactionsSelected,
  isGrouped,
  transactionTotals,
  isTransactionInvalid,
}) => {
  return (
    <TableContainer>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell align="left" component="th" scope="row">
              <Checkbox
                color="primary"
                checked={isAllTransactionsSelected()}
                onClick={(e) => selectAllTransactions(e)}
              />
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold" }}
            >
              Res Id
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold" }}
            >
              Fournisseur
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold" }}
            >
              IBAN
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold" }}
            >
              BIC
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold" }}
            >
              Montant
            </TableCell>
            <TableCell
              component="th"
              scope="row"
              align="center"
              sx={{ fontWeight: "bold" }}
            >
              Compte Ã  debiter
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {Object.entries(
            transactions.reduce(
              (
                acc,
                {
                  Res_Id,
                  nomCrediteur,
                  ibanCrediteur,
                  bicCrediteur,
                  ttc,
                  debitor_id,
                }
              ) => {
                if (!acc[nomCrediteur]) {
                  acc[nomCrediteur] = [];
                }
                acc[nomCrediteur].push({
                  Res_Id,
                  nomCrediteur,
                  ibanCrediteur,
                  bicCrediteur,
                  ttc,
                  debitor_id,
                });
                return acc;
              },
              {}
            )
          ).map(([nomCrediteur, transactions]) => (
            <>
              <TableRow key={nomCrediteur}>
                <TableCell colSpan={7} align="left">
                  <strong>Pour le fournisseur : </strong>
                  {nomCrediteur}
                </TableCell>
              </TableRow>
              {transactions.map(
                ({
                  Res_Id,
                  nomCrediteur,
                  ibanCrediteur,
                  bicCrediteur,
                  ttc,
                  debitor_id,
                }) => (
                  <Tooltip
                    arrow
                    title={
                      isTransactionInvalid(nomCrediteur, ttc)
                        ? `Cette transaction Ã  un montant nÃ©gatif ( ${Number(
                            ttc
                          )} ${DEVISE}) et ne peut pas etre incluse dans le fichier d'export`
                        : `Total transactions : ${
                            isGrouped
                              ? Number(transactionTotals[nomCrediteur])
                              : Number(ttc)
                          } ${DEVISE} pour ${nomCrediteur}`
                    }
                  >
                    <TableRow
                      key={Res_Id}
                      style={
                        isTransactionInvalid(nomCrediteur, ttc)
                          ? { background: "rgba(241, 102, 83, 0.2)" }
                          : { background: "rgba(27, 94, 32, 0.2)" }
                      }
                    >
                      <TableCell component="th" scope="row">
                        <Checkbox
                          checked={
                            isTransactionToBeExported(Res_Id) &&
                            isTransactionInvalid(nomCrediteur, ttc) === false
                          }
                          disabled={isTransactionInvalid(nomCrediteur, ttc)}
                          onClick={selectTransaction(Res_Id)}
                        />
                      </TableCell>
                      <TableCell component="th" scope="row">
                        {Res_Id}
                      </TableCell>
                      <TableCell component="th" scope="row">
                        {nomCrediteur}
                      </TableCell>
                      <TableCell component="th" scope="row">
                        {ibanCrediteur}
                      </TableCell>
                      <TableCell component="th" scope="row">
                        {bicCrediteur}
                      </TableCell>
                      <TableCell component="th" scope="row">
                        {ttc}
                      </TableCell>
                      <TableCell component="th" scope="row">
                        <FormControl
                          fullWidth
                          required={isTransactionToBeExported(Res_Id)}
                          error={
                            debitor_id.length === 0 &&
                            isTransactionToBeExported(Res_Id)
                          }
                        >
                          <InputLabel id={debitor_id}>Debiteur</InputLabel>
                          <Select
                            disabled={isTransactionInvalid(nomCrediteur, ttc)}
                            labelId={debitor_id}
                            value={debitor_id}
                            onChange={handleChangeDebitor(Res_Id)}
                            label="Debiteur"
                            variant="outlined"
                          >
                            {DEBITORS_ACCOUNT.map(({ id, nomDebiteur }) => (
                              <MenuItem key={id} value={id}>
                                {nomDebiteur}
                              </MenuItem>
                            ))}
                          </Select>
                          {debitor_id && (
                            <Container>
                              <div>
                                IBAN :{" "}
                                {
                                  DEBITORS_ACCOUNT.find(
                                    (c) => c.id === debitor_id
                                  ).IBAN
                                }
                              </div>
                              <div>
                                BIC :{" "}
                                {
                                  DEBITORS_ACCOUNT.find(
                                    (c) => c.id === debitor_id
                                  ).BIC
                                }
                              </div>
                            </Container>
                          )}
                        </FormControl>
                      </TableCell>
                    </TableRow>
                  </Tooltip>
                )
              )}
            </>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TransactionsSelector;
